const hideQuestionsBtn = document.querySelector('.btn-warning'); // The yellow button
const themeSwitch = document.querySelector('.switch input'); // The toggle
const feedbackLabel = document.querySelector('.toggle-wrapper span'); // The text label
const backArrow = document.querySelector('.back-arrow'); // Top left arrow

// 2. ACTIVATE "HIDE QUESTIONS" BUTTON
if (hideQuestionsBtn) {
    hideQuestionsBtn.addEventListener('click', () => {
        const sidebar = document.querySelector('.quiz-sidebar');
        sidebar.classList.toggle('hidden');
        
        // Change Button Text
        if (sidebar.classList.contains('hidden')) {
            hideQuestionsBtn.innerText = "Show Questions";
        } else {
            hideQuestionsBtn.innerText = "Hide Questions";
        }
    });
}

// 3. ACTIVATE "DARK MODE" TOGGLE
if (themeSwitch) {
    // Change label text from "Instant Feedback" to "Dark Mode"
    if(feedbackLabel) feedbackLabel.innerText = "Dark Mode";
    
    // Remove 'checked' attribute by default so it starts in Light Mode
    themeSwitch.checked = false;

    themeSwitch.addEventListener('change', (e) => {
        if (e.target.checked) {
            document.body.classList.add('dark-mode');
        } else {
            document.body.classList.remove('dark-mode');
        }
    });
}

// 4. ACTIVATE BACK ARROW
if (backArrow) {
    backArrow.style.cursor = 'pointer';
    backArrow.addEventListener('click', () => {
        if(confirm("Exit quiz? Your progress will be lost.")) {
            window.location.href = 'subjects.html';
        }
    });
}
